    @SuppressLint("StaticFieldLeak")
    public List<Featured> generateItems(int mallId){

        List<Featured> featuredList = new ArrayList<>();

                cd = (ConnectionData) Helper.getItemParam(Constants.KEY_DATA);
                oc = (OdooConnect) Helper.getItemParam(Constants.KEY_OC);

                data = new ArrayList<>();

                data = oc.search_read(
                        "looq_mall.transaction_ads",//api
                        new Object[]
                                {new Object[]//conditions/parameter
                                        {new Object[]{"mall", "=", mallId}}
                                }
                        , Constants.GENERAL
                        , Constants.FIELDS_EVENTS);
                if (data != null) {
                    JSONArray jsonArray = new JSONArray(data);
                    Gson gson = new Gson();
                    Type type = new TypeToken<List<Event>>() {
                    }.getType();
                    listEvent = new ArrayList<>();
                    listEvent = gson.fromJson(String.valueOf(jsonArray), type);

                    for(Event event : listEvent){
                        Featured featured = new Featured();
                        featured.setTitle(event.getName());
                        featured.setDescription(event.getDescription());

                        featuredList.add(featured);
                    }
                }



        return featuredList;
    }

    //new
    public List<Event> generateEvents(int mallId){

        List<Event> eventList = new ArrayList<>();

        cd = (ConnectionData) Helper.getItemParam(Constants.KEY_DATA);
        oc = (OdooConnect) Helper.getItemParam(Constants.KEY_OC);

        data = new ArrayList<>();



        data = new ArrayList<>();
        data = oc.search_read(
                "looq_mall.transaction_ads",//api
                new Object[]
                        {new Object[]//conditions/parameter
                                {new Object[]{"mall", "=", mallId}}
                        }
                , Constants.GENERAL
                , Constants.FIELDS_EVENTS);
        if (data != null && data.size() != 0) {
            JSONArray jsonArray = new JSONArray(data);
            Gson gson = new Gson();
            Type type = new TypeToken<List<Event>>() {
            }.getType();

            if (eventList != null && eventList.size() != 0) {
                eventList.add(gson.fromJson(String.valueOf(jsonArray), type));
            } else {
                eventList = new ArrayList<>();
                eventList = gson.fromJson(String.valueOf(jsonArray), type);
            }
        }

        return eventList;
    }

